"use strict";function narcissistic(t){num_length=t.toString().length,total=0,digits=splitNumber(t);for(var n=0;n<digits.length;n++)total+=Math.pow(digits[n],num_length);return total==t}function splitNumber(t){num_length=t.toString().length,output=[],sNumber=t.toString();for(var n=0,i=sNumber.length;n<i;n+=1)output.push(sNumber.charAt(n));return output}console.log(narcissistic(153));